import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})
export class InputComponent implements OnInit {
  valorItem!: string;
  constructor() { }

  ngOnInit(): void { }
  adicionarItem
}
<form class="add-item" (ngSubmit)="adicionarItem()">
  <input type="text" name="item" placeholder="Digite o item que deseja adicionar" [(ngModel)]="valorItem">
  <button type="submit">Salvar item</button>
</form>
